class Solution {
public:
    bool is_pal(string &s,int start,int end){
        while(start<=end){
            if(s[start++]!=s[end--])
                return false;
        }
        return true;
    }

    void dfs(int index,string &s,vector<string> &path,vector<vector<string>> &res){
        if(index==s.size()){
            res.push_back(path);
            return ;
        }
        for(int i=index;i<s.size();++i){
            if(is_pal(s,index,i)){
                path.push_back(s.substr(index,i-index+1));
                dfs(i+1,s,path,res);
                path.pop_back();
            }
        }
    }
    vector<vector<string>> partition(string s) {
        vector<vector<string>> res;
        if(s.empty())
            return res;
        vector<string> path;
        dfs(0,s,path,res);
        return res;

    }
};
```

