class Solution:
    def removeDuplicates(self, nums: List[int]) -> int:
        if len(nums) <= 1:
            return len(nums)

        write_idx = 2
        for i in range(2,len(nums)):
            if nums[i] != nums[write_idx-2]:
                nums[write_idx] = nums[i] 
                write_idx += 1
        return write_idx
