/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode *curr;
    ListNode *head;

    Solution(ListNode* h) {
        head = h;
        curr=head;
    }

    int getRandom() {
        int temp=curr->val;
        curr=curr->next;

        if(curr==NULL){
            curr=head;
        }
        return temp;
    }
};

