        string sRet;
        int cur = 0;
        while (!sChars.empty()) {
            sRet += toupper(sChars.top());
            sChars.pop();
            cur++;

            if (cur == K && !sChars.empty()) {
                sRet += '-';
                cur = 0;
            }
        }
        reverse(sRet.begin(), sRet.end());
        return sRet;
