public string LicenseKeyFormatting(string S, int K) {
	S = S.Replace("-", "");
	if (String.IsNullOrEmpty(S))
		return String.Empty;

	StringBuilder sb = new StringBuilder();
	int i = S.Length % K;
	if (i > 0)
		sb.Append($"-{S[0..i]}");

	while (i < S.Length)
	{
		sb.Append($"-{S[i..(i + K)]}");
		i += K;
	}

	return sb.ToString()[1..].ToUpper();
}

