	public:

	string licenseKeyFormatting(string s, int k) {

        string str="";
        for(auto &i:s)
        {
            if(i!='-')
                str+=i;
        }

        if(str.size()==0) return str;

        reverse(str.begin(),str.end());
        int extra=str.size()%k;

        string temp="";
        for(int i=0;i<str.size()-extra;i+=k)
        {
            temp+=str.substr(i,k);
            temp.push_back('-');
        }

        if(extra==0)
            temp.pop_back();

        for(int i=str.size()-extra;i<str.size();i++)
            temp+=str[i];

        reverse(temp.begin(),temp.end());
        transform(temp.begin(),temp.end(),temp.begin(),::toupper);

        return temp;

    }
};

