class Solution {
    public String licenseKeyFormatting(String s, int k) {

        s = s.toUpperCase().replace("-", "");

        // number of the first group
        int n = s.length() % k;

        StringBuilder sb = new StringBuilder();

        for (int i=0; i<s.length(); i++) {
            if (i==0) {
                sb.append(s.charAt(i));
                continue;
            }

            // build the first group
            if (n>0&&i<n) {
                sb.append(s.charAt(i));
                continue;
            }

            // first dash after the first group
            if (n>0&&i==n) {
                sb.append('-');
                sb.append(s.charAt(i));
                continue;
            }

			// rest characters after the first dash, each group contains exactly k characters
            if ((i-n) % k == 0) {
                sb.append('-');
                sb.append(s.charAt(i));
            } else {
                sb.append(s.charAt(i));
            }
        }
        return sb.toString();
    }
}

