            for (int i = 0; i < numCourses; i++) {
                if (indegree[i] == 0) 
                    queue.offer(i);
            }

            while (!queue.isEmpty()) {
                Integer prere = queue.poll();
                order[count++] = prere;
                for (Integer course: graph.get(prere)) {
                    if (--indegree[course] == 0) 
                        queue.offer(course);
                }
            }
            if(count == numCourses) {
                return order;
            }
            else {
                return new int[0];
            }
        }
    }


