class Solution {
    func findOrder(_ numCourses: Int, _ prerequisites: [[Int]]) -> [Int] {
        var preCourses = [Int:[Int]]()
        var order = [Int]()
        var isVisited = [Int:Bool]() // store each course visit status

        // store info in dict
        for relation in prerequisites {
            preCourses[relation[0], default: []].append(relation[1])
        }

        for course in 0..<numCourses {
            guard dfs(course, preCourses, &order, &isVisited) else { return [] } // when exists circle, there is no chance to show an order
        }
        return order
    }

    func dfs(_ course: Int, _ preCourses: [Int:[Int]], _ order: inout [Int], _ isVisited: inout [Int:Bool]) -> Bool {
        // check if fully visited;if checked unfully visited, which means existing circles, we return false and terminate searching
        if let fullyVisited = isVisited[course] { return fullyVisited } 

        isVisited[course] = false // when start to visit

        for preCourse in preCourses[course] ?? [] {
            guard dfs(preCourse, preCourses, &order, &isVisited) else { return false }
        }

        isVisited[course] = true // when fully visited
        order.append(course)
        return true
    }
}
