class Solution {
public:
    vector<int> findOrder(int n, vector<vector<int>>& pre) {

        int indegree[2005]={0};
        vector<int>graph[2006];
         for(auto i:pre)
         {
            int x=i[0],y=i[1];
            graph[y].push_back(x); 
            indegree[x]++;
         }

         queue<int>q;
         vector<int>ans;
         for(int i=0;i<n;i++)if(!indegree[i])q.push(i);

         while(!q.empty())
         {
            int t=q.front();q.pop();
            ans.push_back(t);
            for(auto u:graph[t])
             {
                 indegree[u]--;
                 if(indegree[u]==0)q.push(u);
             }
         }

         if(ans.size()!=n)ans.clear();
         //reverse(ans.begin(),ans.end());
         return ans;
    }
};
