import "container/list"

func findOrder(numCourses int, prerequisites [][]int) []int {
    adList := make(map[int][]int, numCourses)
    degree := make([]int, numCourses)
    ans := []int{}
    q := list.New()
    for _, node := range prerequisites {
        adList[node[1]] = append(adList[node[1]], node[0])
        degree[node[0]]++
    }
    for i:=0; i<numCourses; i++ {
        if degree[i] == 0 {
            q.PushBack(i)
        }
    }
    for q.Len() > 0 {
        f := q.Front().Value.(int)
        ans = append(ans, f)
        q.Remove(q.Front())
        numCourses--
        for _, d := range adList[f] {
            degree[d]--
            if degree[d] == 0 {
                q.PushBack(d)
            }
        }
    }
    if numCourses == 0 {
        return ans
    }
    return []int{}
}
