		adj[prerequisites[i][0]].push_back(prerequisites[i][1]);
	}

	for(int v = 0; v < numCourses; ++v) {
        if(color[v]==0) { 
            if (!DFS(v, adj, res))  return {}; 
        }
	}
	return res;
    }
};
