class Solution {
public:
     vector<int> topological_sort(int n, vector<vector<int>>& edges) {
      // convert to adjacency lists, and record in-degrees
      vector<vector<int>> adj(n);
      vector<int> in_degree(n);
      for (auto &p : edges) {
        adj[p[1]].push_back(p[0]);
        in_degree[p[0]]++;
      }

      // initialize our todo queue with all the in_degree=0 nodes
      queue<int> todo;
      for (int i = 0; i < n; ++i) {
        if (in_degree[i] == 0)
          todo.push(i);
      }

      // traverse our graph
