class CycleError(Exception):
    pass


class Solution:
    def findOrder(self, n: int, prerequisites: List[List[int]]) -> bool:
        graph = [[] for _ in range(n)]
        for p in prerequisites:
            graph[p[1]].append(p[0])

        visited = set()  # All visited elements.
        ordered = []     # Ordered returns DAG order traversal.

        def search(node, parents):
            if node in parents:
                raise CycleError(node)
            if node in visited:
                return

            visited.add(node)
            parents = parents.union((node,))
            for adj in graph[node]:
                search(adj, parents)

            ordered.insert(0, node)

        try:
            for node in range(n):
                search(node, set())
            return ordered
        except CycleError:
            return []
