class Solution {
public:
    vector<int> findOrder(int num, vector<pair<int, int>>& pres) {
        vector<vector<int>> graph(num);
        vector<int> indegrees(num, 0), v;
        stack<int> toVisit;
        int count = 0;
        for(auto& pair: pres)
        {
            graph[pair.second].push_back(pair.first);
            indegrees[pair.first]++;
        }
        for(int i = 0; i < num; i++)
            if(!indegrees[i]) toVisit.push(i);
        while(!toVisit.empty())
        {
            int from = toVisit.top();
            toVisit.pop();
            for(auto n: graph[from])
                if(--indegrees[n] == 0) toVisit.push(n);
            count++;
            v.push_back(from);
        }
        return count==num? v : (v.clear(), v);
    }
};

