class Solution {
public:
bool bfs(vector<vector<int>> adj,int V,stack<int> &st)
    {
        vector<int> indegree(V,0);
        for(int i=0;i<V;i++)
        {
            for(auto it:adj[i])
            {
                indegree[it]++;
            }
        }
        for(int i=0;i<V;i++)
        {
            int j=0;
            for(;j<V;j++)
            {
                if(indegree[j]==0)
                {  
                    st.push(j);
                    break;
                }
            }
            if(j==V)return false;
            indegree[j]--;
            for(auto it:adj[j])
            {
                indegree[it]--;
            }
        }
        return true;
    }
