class Solution {
public:
    vector<int> findOrder(int n, vector<vector<int>>& prerequisites) {
        //Make The Graph
        vector<vector<int>> Graph(n);
        for(int i = 0 ; i < prerequisites.size() ; i++){
            Graph[prerequisites[i][0]].push_back(prerequisites[i][1]);
        }

        //I have Graph at this point...
        //i will make a dfs call on my graph and check whether there is any cycle in my graph or not.
        //it there is a cycle i can say that topo sort will not exist and return empty vector
        //else i will return the topo sort that i will get after dfs call(because toposort is same as 
        //postorder traveral)
        vector<int> visited(n,0);
        //0 means that the given vertex is unvisited
        //1 means that the given vertex is in the path
        //2 means that the given vertex is processed and not the part of the given path
        vector<int> ans;
        function<int(int)> dfs = [&](int u){
            // mark
            visited[u] = 1;
            //for each not processed children make a dfs call.
            //if the vertex has visited[v] = 1 menas it is part of the current path so there is a cycle
            for(int i = 0 ; i < Graph[u].size() ; i++){
                int v = Graph[u][i];
                if(!visited[v]){
                    if(dfs(v) == -1)
                        return -1;
                }else if(visited[v] == 1){
                    //there is a cycle
                    return -1;
                }
            }
            visited[u] = 2;
            ans.push_back(u);
            return 2;
        };
        for(int i = 0 ; i < n ; i++){
            if(!visited[i]){
                if(dfs(i) == -1)
                    return {};
            }
        }
        return ans;
    }
};
