class Solution {
    public int[] findOrder(int numCourses, int[][] pre) {
        if(numCourses==1){
            return new int[] {0};
        }
        Graph g = new Graph(numCourses);
        for(int i=0; i<pre.length; i++){
            g.addEdge(pre[i][1], pre[i][0]);
        }
        return g.topSort();
    }

    class Graph {
        class Node {
            int id;
            LinkedList<Node> adjacents = new LinkedList<>();

            Node(int id) {
                this.id = id;
            }
        }

        int V;

        Graph(int v) {
            this.V = v;
            for(int i=0; i<V; i++){
                nodeLookup.put(i, new Node(i));
            }
        }

        HashMap<Integer, Node> nodeLookup = new HashMap<>();

        Node getNode(int id) {
            if (nodeLookup.containsKey(id))
                return nodeLookup.get(id);
            else {
                Node newNode = new Node(id);
                nodeLookup.put(id, newNode);
                return newNode;
            }
        }

        void addEdge(int source, int dest) {
            Node s = getNode(source);
            Node d = getNode(dest);

            s.adjacents.add(d);
        }

        boolean checkCycle() {
            boolean visited[] = new boolean[V];
            boolean tracker[] = new boolean[V];
            for (Map.Entry<Integer, Node> entry : nodeLookup.entrySet()) {
                boolean loop = DFS(visited, tracker, entry.getKey());
                if (loop) {
                    return true;
                }
            }
            return false;
        }

        boolean DFS(boolean[] visited, boolean[] tracker, int current) {
            if (tracker[current]) {
                return true;
            }
            visited[current] = true;
            tracker[current] = true;

            Node node = getNode(current);
            for (Node neighbor : node.adjacents) {
                boolean res = DFS(visited, tracker, neighbor.id);
                if (res) {
                    return true;
                }
            }
            tracker[current] = false;

            return false;
        }

        int[] topSort() {
            if (checkCycle()) {
                return new int[] {};
            }
            Stack<Integer> result = new Stack<>();
            HashSet<Integer> visited = new HashSet<>();
            for (Map.Entry<Integer, Node> entry : nodeLookup.entrySet()) {
                topSortHelper(entry.getKey(), visited, result);
            }
            int res[] = new int[result.size()];
            for (int i = 0; i < res.length; i++) {
                res[i] = result.pop();
            }
            return res;
        }

        void topSortHelper(int id, HashSet<Integer> visited, Stack<Integer> result) {
            if (visited.contains(id)) {
                return;
            }
            visited.add(id);
            for (Node adjacent : getNode(id).adjacents) {
                topSortHelper(adjacent.id, visited, result);
            }
            result.push(id);
        }
    }
}
