class Solution {
public:
    vector<int> findOrder(int n, vector<vector<int>>& p) {
        vector <int> cnt(n, 0), ret;
        vector <vector <int>> edge(n); // prerequisite edges
        for (auto &z : p) {
            int x = z[0], y = z[1];
            edge[y].push_back(x); // after taking course 'y' we might want to take course 'x'
            cnt[x]++; // indegree up, means there are cnt[x] courses we need to take before taking course 'x'
        }
        for (int i = 0; i < n; i++)
            if (cnt[i] == 0) // every course that doesn't have prerequisites we can add to the queue and take these courses
                ret.push_back(i); // put courses we can take into our return array
        for (int i = 0; i < ret.size(); i++) // go by the courses we can take
            for (auto &x : edge[ret[i]]) // check every other course we might want to take from the current course
                if (--cnt[x] == 0) // reduce prerequisite count
                    ret.push_back(x);  // add courses to the queue if they don't have any more prerequisites
        return ret.size() == n ? ret : vector <int> (); // if 'ret' has 'n' elements means we were able to take all courses, otherwise we couldn't.
    }
};
