class Solution {
public:
    //using topological sort
    vector<int> findOrder(int n, vector<vector<int>>& preq) {
        vector<int> adj[n];
        vector<int> indegree(n);    
        vector<int> ans;

        for(auto i : preq) {
            adj[i[1]].push_back(i[0]);
            indegree[i[0]]++;              
        }

        queue<int> q;
        for(int i = 0; i<n; i++) {
            if(indegree[i] == 0) {
                q.push(i);
            }
        }

        while(!q.empty()) {
            int node= q.front();
            q.pop();
            ans.push_back(node);

            for(auto v : adj[node]) {
                indegree[v]--;
                if(indegree[v] == 0) q.push(v);
            }
        }

        return ans.size() == n ? ans : vector<int> {};

    }
};
