func findOrder(numCourses int, prerequisites [][]int) []int {
    edges, indegree := make([][]int, numCourses), make([]int, numCourses)
    for _, v := range prerequisites {
        edges[v[1]] = append(edges[v[1]], v[0])
        indegree[v[0]]++
