        cur := queue[0]
        queue = queue[1:]
        numCourses--
        res = append(res, cur)
        for _, v := range edges[cur] {
            indegree[v]--
            if indegree[v] == 0 { queue = append(queue, v) }
        }
    }
    if numCourses != 0 { return []int{} }
    return res
}
