Note: it is a vanilla toposort with Kahn's BFS algorithm. The conversion of the final result from list to array is a bit ugly.

    public class Solution {
    	public int[] findOrder(int numCourses, int[][] prerequisites) {
    		// BFS toposort with Kahn's algorithm
    		int[] indegree = new int[numCourses];
    		for (int[] p : prerequisites) {
    			indegree[p[0]]++;
    		}
    		// enqueue vertices with indegree == 0, no prerequisite is needed
    		LinkedList<Integer> queue = new LinkedList<Integer>();
    		for (int i = 0; i < indegree.length; i++) {
    			if (indegree[i] == 0) {
    				queue.offer(i);
    			}
    		}
    		List<Integer> result = new ArrayList<Integer>();
    		while (queue.size() != 0) {
    			Integer curr = queue.poll();
    			result.add(curr);
    			for (int[] p : prerequisites) {
    				if (p[1] == curr) {
    					indegree[p[0]]--;
    					if (indegree[p[0]] == 0) {
    						queue.offer(p[0]);
    					}
    				}
    			}
    		}
    		if (result.size() != numCourses) {
    			return new int[0];
    		}
    		int[] finalResult = new int[result.size()];
    		for (int i = 0; i < result.size(); i++) {
    			finalResult[i] = result.get(i);
    		}
    		return finalResult;
    	}
    }

