        for(auto i:adj){
            for(auto nbr:i.second)
                indegree[nbr]++;
        }
        //put all node inside q with in degree 0 
        for(int i=0;i<n;i++){
            if(indegree[i]==0)
                q.push(i);
        }

        while(!q.empty()){
            int fnode=q.front();
            q.pop();
            ans.push_back(fnode);
            for(auto nbr : adj[fnode]){
                indegree[nbr]--;
                    //check for zero again
                    if(indegree[nbr] == 0) {
                        q.push(nbr);
                    }
            }
        }
        if(ans.size()==n)
            return ans;
        else
            return {};
    }
    vector<int> findOrder(int numCourses, vector<vector<int>>& prerequisites) {
        unordered_map<int,list<int>> adj;
        for(auto task:prerequisites){
            int u=task[0];
            int v=task[1];
            adj[u].push_back(v);
        }
        vector<int> ans= topoSortBFS(numCourses,adj);
        reverse(ans.begin(),ans.end());
        return ans;
    }
};
