```
class Solution {
public:
    vector<int> findOrder(int numCourses, vector<vector<int>>& prerequisites) {
        int& v = numCourses; // alias

        vector<int> adj[v];

        // building adjacency list
        for (auto& pr : prerequisites) {
            adj[pr[1]].emplace_back(pr[0]);
        }

        vector<bool> visited(v, false), pathVisited(v, false);
        vector<int> result;

        for (int node = 0; node < v; node++) {
            if (!visited[node] && dfs(adj, visited, pathVisited, result, node))
                return {};
        }

        reverse(result.begin(), result.end());
        return result;
    }

private:
    bool dfs(vector<int> adj[], vector<bool>& visited, vector<bool>& pathVisited, 
                vector<int>& result, int node)
    {
        if (pathVisited[node])
            return true;    
        if (visited[node])
            return false;

        visited[node] = pathVisited[node] = true;

        for (auto& adjNode : adj[node]) {
            if (dfs(adj, visited, pathVisited, result, adjNode))
                return true;
        }

        pathVisited[node] = false;
        result.emplace_back(node);
        return false;
    }
};
```

# Kahn's Algorithm
```
class Solution {
public:
    vector<int> findOrder(int numCourses, vector<vector<int>>& prerequisites) {
        int& v = numCourses; // alias

        vector<int> adj[v], result(v), indegree(v);
        int topoLen = 0;

        // building adjacency list
        for (auto& pr : prerequisites) {
            adj[pr[1]].emplace_back(pr[0]);
        }

        for (int node = 0; node < v; node++) {
            for (auto& adjNode : adj[node])
                indegree[adjNode]++;
        }

        queue<int> Q;

        for (int node = 0; node < v; node++) {
            if (indegree[node] == 0)
                Q.emplace(node);
        }

        int i = 0;
        while (!Q.empty()) {
            int node = Q.front(); Q.pop();
            result[i++] = node;
            topoLen++;

            for (auto& adjNode : adj[node]) {
                if (--indegree[adjNode] == 0)
                    Q.emplace(adjNode);
            }
        }

        if (topoLen != v) return {};
        return result;
    }
};
```

