class Solution:
    def findOrder(self, numCourses: int, prerequisites: List[List[int]]) -> List[int]:
        ## Dependancy of each node/course
        indegree= [0]*numCourses

        result = []
        ##Adjacency list
        mapper = defaultdict(list)

        ##Putting all the courses that doesn't have any dependancy in queue
        for ai, bi in prerequisites:
            mapper[bi].append(ai)
            indegree[ai]+=1
        # print(mapper,indegree)

        q = deque()

        for i in range(len(indegree)):
            if indegree[i]==0:
                q.append(i)
        while(q):
            node = q.popleft()
            result.append(node)
            for neigh in mapper[node]:
            # One less dependancy
                indegree[neigh]-=1
                if indegree[neigh]==0:
                    q.append(neigh)
        if len(result) == numCourses:
            return result
        return []        
