            cycle += course
            preReq[course]!!.forEach { if (!dfs(it)) return false }
            cycle -= course

            visited += course
            ans += course
            return true
        }

        repeat(numCourses) { if (!dfs(it)) return intArrayOf() }

        return ans.toIntArray()
    }
}
```

