```
class Solution(object):
    def findRedundantDirectedConnection(self, edges):
        #有向图也可以使用union find 方法！
        N = len(edges) + 1
        par = {i:i for i in range(N)}
        def find(x):
            if par[x] != x:
                par[x] = find(par[x])
            return par[x]

        directpar = [0] * N
        edge1 = None
        edge2 = None
        lastEdgeCauseCycle = None

        for u, v in edges:
            # from u to v, i.e., u=parent, v=child
            if directpar[v] != 0:
                edge1 = [directpar[v], v]
                edge2 = [u, v]
            else:
                directpar[v] = u # do not forget this line!
                ru = find(u)
                rv = find(v)
                if ru != rv:
                    par[ru] = rv
                else:
                    lastEdgeCauseCycle = [u,v]

        if edge1 and edge2:
            # edge2 was not added (it is ignored, if still lastEdgeCauseCycle != None, the bad one is edge1)
            return edge1 if lastEdgeCauseCycle else edge2
        else:
            return lastEdgeCauseCycle
