```
class UnionFind:
    def __init__(self, N):
        self.parent = [-1] * N
        self.rank = [0] * N
        self.count = 0
        for i in range(N):
            self.parent[i] = i
            self.count += 1

    def findRoot(self, i):
        if self.parent[i] != i:
            self.parent[i] = self.findRoot(self.parent[i]) # path compression
        return self.parent[i]

    def union(self, x, y):
        rootx, rooty = self.findRoot(x), self.findRoot(y)
        if rootx == rooty:
            return False
        rankx, ranky = self.rank[rootx], self.rank[rooty]
        if rankx < ranky:
            self.parent[rootx] = rooty
        elif rankx > ranky:
            self.parent[rooty] = rootx
        else:
            self.parent[rootx] = rooty
            self.rank[rooty] += 1
        return True # could successfully combined

class Solution:
    def findRedundantDirectedConnection(self, edges):
        directParent = [0]*(len(edges) + 1) # since node's NO. starts from 1
        uf = UnionFind(len(edges) + 1)
        edge1 = None
        edge2 = None
        lastEdgeCauseCircle = None
        for pair in edges:
            u = pair[0]
            v = pair[1]
            if directParent[v] != 0:
                # node v already has a father, so we just skip the union of this edge and check if there will be a circle
                edge1 = [directParent[v], v]
                edge2 = pair # specially note that we skipped edge2 HERE for UnionFind!
            else:
                directParent[v] = u
                rootu = uf.findRoot(u)
                rootv = uf.findRoot(v)
                if rootu != rootv:
                    uf.union(u, v)
                else:
                    lastEdgeCauseCircle = pair
        if edge1 and edge2: # this means that we found in-degree = 2 node 
            return edge2 if not lastEdgeCauseCircle else edge1
			# when there is a circle, lastEdgeCauseCircle is not None, and edge1 is the problem;
			# when no circle, lastEdgeCauseCircle is None, and edge2 was the problem which we intentionally skipped during the loop!
        else: # i.e., there is no in-degree=2 node
            return lastEdgeCauseCircle # case 1, no in-degree=2 nodes
