          }
        }

        queue<int>q;
        for(int i=1;i<=n;i++)
        {
            if(indegree[i]==0)
            q.push(i);

            if(indegree[i]>=2)
            {
                return false;
            }

        }

        while(!q.empty())

        {
            int node=q.front();
            q.pop();
            ans.push_back(node);

            for(auto it:mp[node])
            {
                indegree[it]--;
                if(indegree[it]==0)
                {
                    q.push(it);
                }

            }

        }

        if(ans.size()!=n)
        {
            return false;
        }

        return true;

