    DSU(int n){
        p.resize(n,-1);
        for(int i=0;i<n;i++){
            make_set(i);
        }
    }
    int find(int a){
        if(p[a]==a)return a;
        p[a] = find(p[a]);
        return p[a];
    }
    void union_set(int a,int b){
        a = find(a);
        b = find(b);
        if(a==b)return;
        p[b] = a;
    }
    void make_set(int a){
        p[a] = a;
