public:
    vector<int> detectCycle(vector<vector<int>>& edges,vector<int>&e){
        int n = edges.size();
        DSU *dsu = new DSU(n+1);
        for(int i=0;i<n;i++){
            if(edges[i]==e)continue;
            int a = edges[i][0],b = edges[i][1];
            if(dsu->find(a)==dsu->find(b))return edges[i];
            dsu->union_set(a,b);
        }
        return {0,0};
