class Solution {
public:
    bool fun(int node,int &n,vector<int>&vis,vector<vector<int>>&g, int s, int e){
        vis[node]=1;n--;
        if(n==0) return 1; // Indicates all nodes are visited
        for(auto a:g[node]){
            if(node==s&&a==e)continue;         
            if(vis[a]==0&&fun(a,n,vis,g,s,e)) return 1;
        }    
        return 0;
    }
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& e) {
        int n=e.size();
        vector<int>in(n+1);  // Indegree vector is used to find root node
		vector<vector<int>>g(n+1);
       for(auto a:e){
           in[a[1]]++; g[a[0]].push_back(a[1]);
       }
        vector<int>vis(n+1); // used to escape from infinate loops if graph has cycle
        for(int k=e.size()-1;k>=0;k--){ // Travel from last edge
            in[e[k][1]]--;; // removing  edge
            for(int i=1;i<=n;i++){
                int p=n; 
                if(in[i]==0) { // checking for root node which has indegree 0
                  int b=fun(i,p,vis,g,e[k][0],e[k][1]);               
                    if(b==1) return e[k];
                   for(int j=1;j<=n;j++)vis[j]=0;
                    break; // breaking because only one root exist
                }               
            }
            in[e[k][1]]++;
        }
        return {};
    }
};
