public int[] findRedundantDirectedConnection(int[][] edges) {
	int n = edges.length;

	/* When a node has 2 incoming edges, then one of those 
	edges is redundant */
	int[] indegree = new int[n + 1];
	int found = -1;
	for (int[] edge : edges) {
		indegree[edge[1]]++;
		if (indegree[edge[1]] == 2) {
			found = edge[1];
			break;
		}
	}
	/**
		If no node with multiple parent, then look for cycles
	**/
	if (found == -1)
		return detectCycle(edges, new int[2]);

	/**
		When both happens, which means cycle and multiple parents both exist
	**/
	for (int i = n - 1; i >= 0; i--) {
		if (edges[i][1] == found) {
			int[] redundant = detectCycle(edges, edges[i]);
			// No cycle found if I skip the current edge
			if (redundant == null) 
				return edges[i];
		}
	}
	return new int[0];
}

private int[] detectCycle(int[][] edges, int[] skip) {
	UnionFind uf = new UnionFind(edges.length);
	for (int[] edge : edges) {
		if (edge[0] == skip[0] && edge[1] == skip[1]) continue;
		if (!uf.union(edge[0], edge[1])) return edge;
	}
	return null;
}

class UnionFind {
	int[] parent;
	int[] rank;
	UnionFind(int n) {
		parent = new int[n + 1];
		for (int i = 1; i <= n; i++)
			parent[i] = i;
		rank = new int[n + 1];
	}
	int find(int a) {
		if (parent[a] != a)
			parent[a] = find(parent[a]);
		return parent[a];
	}
	boolean union(int a, int b) {
		int p1 = find(a);
		int p2 = find(b);
		if (p1 == p2) return false;
		if (rank[p1] > rank[p2]) {
			rank[p1]++;
			parent[p2] = p1;
		} else {
			rank[p2]++;
			parent[p1] = p2;
		}
