class Solution {
public:
    class unionfind{
        int *parent;
        int *size;
        public:
        unionfind(int n){
            parent= new int[n+1];
            size= new int[n+1];
            for(int i=0;i<=n;i++){
                parent[i]=i;
                size[i]=1;
            }
        }
        int find(int a){
            if(a!=parent[a]){
                parent[a]=find(parent[a]);
            }
            return parent[a];
        }
        bool unionop(int a,int b){
            int x=find(a);
            int y=find(b);
            if(x==y){
                return false;
            }
            if(size[x]>size[y]){
                parent[y]=x;
                size[x]+=size[y];
            }else{
                parent[x]=y;
                size[y]+=size[x];
