class Solution {
    public int[] findRedundantDirectedConnection(int[][] edges) {
        int[] parent = new int[edges.length + 1];
                int[] candidate1 = null, candidate2 = null;
        for (int[] edge : edges) {
            int p1 = find(edge[0], parent);
            int p2 = find(edge[1], parent);
            //this edge makes a cicle
            if (p1 == p2) {
                candidate2 = edge;
            } else {
                // this edge means edge2 has more than one parents
                if (p2 != edge[1]) {
                    candidate1 = edge;
                } 
                // union
                else {
                    parent[p2] = p1;
                }
            }
        }
        // the tree contains cycle, remove the edge that makes it a cycle
        if (candidate1 == null) {
            return candidate2;
        }

        // the tree has no cycle but one nodes contain two parents, remove that edge
        if (candidate2 == null) {
            return candidate1;
        }

        // the tree contains both cycle and a node that have more than two parents, remove the first edge for that node
        for (int[] edge: edges) {
            if (edge[1] == candidate1[1]) {
                return edge;
            }
        }
        return null;
    }

    public int find(int node, int[] parent) {
        while (parent[node] != 0) {
            node = parent[node];
        }
        return node;
    }
}
