class Solution:    
    def check(self, g, ideg):

        # if I remove this edge...
        # Graph should be connected...
        # Every edge should have 1 parent, i.e 1 ideg and 
        # only root should have 0 
        # if true, nikaloooo bc (Remove the edge manh...)

        root = None
        for i in ideg:
            if ideg[i] == 0:
                root = i
            if ideg[i] > 1:
                return False #the curr removed choice is wrong..
        if root is None:
            return False #curr removed choice is wrong...
        # dfs to see if graph is connected...
        vis = set()
        def dfs(root):
            vis.add(root)
            if root not in g:
                return
            for nei in g[root]:
                if nei not in vis:
                    dfs(nei)

        dfs(root)
        if len(vis) == self.n:
            return True
        return False

    def findRedundantDirectedConnection(self, edges: List[List[int]]) -> List[int]:
        g = {}
        self.n = len(edges)
        ideg = {i:0 for i in range(1, self.n + 1)}
        for u, v in edges:
            g[u] = g.get(u, []) + [v]
            ideg[v] = 1 + ideg.get(v, 0)

        for u,v in edges[::-1]:
            g[u].remove(v)
            ideg[v] -= 1
            if self.check(g,ideg):
                return [u,v]
            g[u].append(v)
            ideg[v] += 1
        return []
