// condition 1: one node 2 parents, we will try to remove one node. If we remove wrong node, a circle will be created
// condition 2: a perfect circle without any node having 2 parents, in that case we just remove the last edge that creates circle

var findParent = function(parent, i) {
  if (parent[i] !== i) {
    parent[i] = findParent(parent, parent[i]);
  }
  return parent[i];
};

var findRedundantDirectedConnection = function(edges) {
  var parent = [];
  //detect circle
  for (let i = 1; i <= edges.length; i++) {
    parent[i] = i;
  }
  let circleEdge, removedEdge, candidateEdge;
  for (let i = 0; i < edges.length; i++) {
    let [u, v] = edges[i];
    let pu = findParent(parent, u);
    let pv = findParent(parent, v);
    if (pv !== v) {
      removedEdge = [u, v]; // node with 2 parents
    } else {
      if (pv == pu) {
        circleEdge = [u, v]; // circle edge
      }
      parent[v] = pu;
    }
  }
  if (!removedEdge) {
    return circleEdge;
  }
  if (circleEdge) {
    return edges.find(d => d[1] == removedEdge[1] && d[0] !== removedEdge[0]);
  } else {
    return removedEdge;
  }
};
