class Solution {
    public int[] findRedundantDirectedConnection(int[][] edges) {
        int[] roots = new int[edges.length + 1];
        for (int i = 0; i < roots.length; i++) {
            roots[i] = i;
        }
        int[] circle = null;
        int[] doubleParents = null;
        for (int[] edge : edges) {
            int node1 = find(roots, edge[0]);
            int node2 = find(roots, edge[1]);
            if (node2 != edge[1]) { //double parents
                doubleParents = edge;
                continue;
            }
            if (node1 == node2) { //circle
                circle = edge;
                continue;
            }
            roots[node2] = node1;
        }
        if (doubleParents == null) return circle;  //only have circle then return last edge
        if (circle == null) return doubleParents; //only have double parents then return last parent
        for (int[] tmp : edges) { //have double parents and circle, then return the parent in circle
            if (tmp[1] == doubleParents[1]) return tmp;
        }
        return edges[edges.length - 1];
    }

    public int find(int[] roots, int node) {
        while (roots[node] != node) {
            roots[node] = roots[roots[node]];
            node = roots[node];
        }
        return node;
    }
}
