class Solution {
public:
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        int n = edges.size();
        vector<int> parent(n + 1, 0);
        vector<int> candidateA, candidateB;

        // Step 1: Detect the candidates for removal
        for (const auto& edge : edges) {
            int parentOfChild = edge[0];
            int child = edge[1];

            if (parent[child] == 0) {
                // If the child node has no parent, assign the current edge as its parent
                parent[child] = parentOfChild;
            } else {
                // If the child already has a parent, it's a candidate for removal
                candidateA = {parent[child], child};
                candidateB = edge;

                // Temporarily remove the current edge by setting the child to 0
                edge[1] = 0;
            }
        }

        // Step 2: Reset parent pointers for the union-find operation
        for (int i = 1; i <= n; i++) {
            parent[i] = i;
        }

        // Step 3: Detect if there's a loop and return the appropriate candidate
        for (const auto& edge : edges) {
            if (edge[1] == 0) {
                // Skip edges that were temporarily removed
                continue;
            }

            int parentOfSource = edge[0];
            int child = edge[1];
            int parentOfSourceRoot = findRoot(parent, parentOfSource);

            if (parentOfSourceRoot == child) {
                // A loop is detected; return the appropriate candidate
                if (candidateA.empty()) {
                    return edge;
                } else {
                    return candidateA;
                }
            }

            // Perform the union operation by updating the parent
            parent[child] = parentOfSourceRoot;
        }

        // If no loop was detected, return candidate B
        return candidateB;
    }

private:
    int findRoot(vector<int>& parent, int node) {
        if (parent[node] != node) {
            parent[node] = findRoot(parent, parent[node]);
        }
        return parent[node];
    }
};

