class Solution
{
    public int[] findRedundantDirectedConnection(int[][] edges)
    {
    	/* two scenario:
    	 * 0\xBA all nodes with only 1 in-degree except one node with two in-degree (the extra edge causes)
    	 * 	  for this situation, find the edges who point to this node (two edges)
    	 * 	  delete one of the two edges and check if the other edges form a good tree
    	 *    GOOD TREE: the edge is the one to delete
    	 *    NOT TREE: the other edge should be deleted.
    	 * 
    	 * 1\xBA all nodes with only one in-degree.
    	 *    then the extra edge will cause a circle
    	 *    we can use union find to search for the extra edge.
    	 */
    	// 0\xBA find the in-degree of all nodes.
    	int[] degreeOfNodes = new int[edges.length + 1];
    	int nodeWithTwoDegree = -1;
    	for (int i = 0; i < edges.length; i++)
    	{
    		degreeOfNodes[edges[i][1]]++;
    		if (degreeOfNodes[edges[i][1]] == 2)
    		{
    			// found the node with two-in-Degrees.
    			nodeWithTwoDegree = edges[i][1];
    			break;
    		}
    	}

    	// for union find use.
    	int[] parents = new int[edges.length + 1];
        for (int i = 0; i < parents.length; i++)
            parents[i] = i;

    	if (nodeWithTwoDegree != -1)
    		return findRedundantWithTwoInDegree(edges, nodeWithTwoDegree, parents);

    	// 1\xBA No 2-in-degree nodes, circular exists. union find to search for extra edge.
    	for (int i = 0; i < edges.length; i++)
    	{
    		// the two nodes in the same set already, not a corret tree, return the other node
    		int parentOfFrom = findParent(parents, edges[i][0]);
    		int parentOfTo = findParent(parents, edges[i][1]);
    		if (parentOfFrom == parentOfTo)
    			return edges[i];

    		parents[parentOfTo] = parentOfFrom;
    	}

    	return null;
    }

    private int[] findRedundantWithTwoInDegree(int[][] edges, int nodeWithTwoDegree, int[] parents)
    {
    	// 0\xBA find the two edge that point to nodeWithTwoDegree.
    	int[] theTwoEdges = new int[2];
    	int index = 0;
    	for (int i = 0; i < edges.length && index < 2; i++)
    	{
    		if (edges[i][1] == nodeWithTwoDegree)
    			theTwoEdges[index++] = i;
    	}

    	// 1\xBA check to delete one of the edges to see if left edges can form a tree
    	//    begin with the later one first.
    	for (int i = 0; i < edges.length; i++)
    	{
    		if (i == theTwoEdges[1])
    			continue;

    		// the two nodes in the same set already, not a corret tree, return the other node
    		int parentOfFrom = findParent(parents, edges[i][0]);
    		int parentOfTo = findParent(parents, edges[i][1]);
    		if (parentOfFrom == parentOfTo)
    			return edges[theTwoEdges[0]];

    		parents[parentOfTo] = parentOfFrom;
    	}

    	return edges[theTwoEdges[1]];
    }

    // find root node of current node.
    private int findParent(int[] parents, int child)
    {
    	if (child == parents[child])
    		return child;

    	return findParent(parents, parents[child]);
    }
}
