class Solution {
    public class UnionFind{
        int[] parent;
        int[] rank;
        UnionFind(int vtces){
            parent=new int[vtces];
            rank=new int[vtces];
            for(int i=0;i<vtces;i++){
                parent[i]=i;
            }
            Arrays.fill(rank,0);
        }
        int find(int x){
            if(parent[x]==x){
                return x;
            }
            return parent[x]=find(parent[x]);
        }
        boolean union(int x,int y){
            int lx=find(x);
            int ly=find(y);

            if(lx!=ly){
                int rank1=rank[lx];
                int rank2=rank[ly];

                if(rank1>rank2){
                    parent[ly]=lx;
                }else if(rank2>rank1){
                    parent[lx]=ly;
                }else{
                    parent[ly]=lx;
                    rank[lx]++;
                }
                return false;
            }else{
                return true;
            }
        }
    }
    public int[] findRedundantDirectedConnection(int[][] edges) {
        int[] indegree=new int[edges.length+1];
        Arrays.fill(indegree,-1);
        int bl1=-1;
        int bl2=-1;
        for(int i=0;i<edges.length;i++){
            int[] edge=edges[i];
            int u=edge[0];
            int v=edge[1];
            if(indegree[v]==-1){
                indegree[v]=i;
            }else{
                bl1=i;
                bl2=indegree[v];
            }
        }
        UnionFind uf=new UnionFind(edges.length+1);
        for(int i=0;i<edges.length;i++){
            if(i==bl1){
                continue;
            }
            int u=edges[i][0];
            int v=edges[i][1];

            boolean flag=uf.union(u,v);

            if(flag==true){//cycle case
                if(bl1==-1){
                    return edges[i]; //only cycle
                }else{
                    return edges[bl2];//2 parent + cycle
                }
            }
        }
        return edges[bl1];// 2 parent
    }
}
