// union find

// The graph has n nodes and n edges, meaning there must be a cycle
// With the extra egde: the graph will have 2 cases
// case 1: there is one and only one node with indegree of 2. In this case, it must be one of 
// the two in edges of this node to be removed
// case 2: each node has at most 1 indegree. in this case, iterate all edges, the extra edge will 
// create a cycle.


class Solution {
    int[] parent;

    public int[] findRedundantDirectedConnection(int[][] edges) {
        int n = edges.length;

        parent = new int[n + 1];
        Arrays.fill(parent, -1);

        int[] inDegree = new int[n + 1];

        for (int[] e: edges) inDegree[e[1]]++;

        int node = -1;
        for (int i = 1; i <= n; i++) {
            if (inDegree[i] == 2) {
                node = i;
                break;
            }
        }
        // Case 1: there is one and only one node, n, with indegree of 2, so one of them must be removed.
        if (node != -1) {
            // union all edges except for the in edges of node n
            for (int[] e: edges) {
                int u = e[0];
                int v = e[1];
                if (v == node) continue;
                union(u, v);
            }
            for (int[] e: edges) {
                int u = e[0];
                int v = e[1];
                if (v == node) {
                    if (find(u) != find(v)) {
                        union(u, v);
                    } else {
                        return e;
                    }
                }
            } 
            return null;
        }

        // Case 2: there is no nodes with indegree greater than 1
        for (int[] e: edges) {
            int u = e[0];
            int v = e[1];
            if (find(u) == find(v)) return e;
            union(u, v);
        }
        return null;
    }

    private int find(int x) {
        int r = x;
        while (parent[r] >= 0) {
            r = parent[r];
        }

        if (r == x) return r;
        int p = parent[x];
        while (p >= 0) {
            parent[x] = r;
            x = p;
            p = parent[x];
        }
        return r;
    }

    private void union(int x, int y) {
        int px = find(x);
        int py = find(y);
        if (px == py) return;

        if (parent[px] > parent[py]) {
            parent[py] += parent[px];
            parent[px] = py;
        } else {
            parent[px] += parent[py];
            parent[py] = px;
        }
    }
}
