```
class Solution {
public:
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        const int n = edges.size();

        // The parent vector used for the union-find set.
        parent.resize(n + 1, 0);
        for (int i = 1; i <= n; ++i) {
            parent[i] = i;
        }

        // pre[i] = j: there is a directed edge (j, i).
        vector<int> pre(n + 1, -1);

        // After the for-loop below, u == -1 if and only if there is no node with indegree 2.
        // If there is a node with indegree 2, then (pre_u, u) is its second inbound edge.
        // Note:
        // - There will be at most 1 node with indegree 2: this is because one extra edge can add only
        //   1 indegree to the graph
        // - There can be 0 node with indegree 2: this will happen if and only if the extra edge is
        //   pointing to the root of the tree
        // - When u != -1, its first inbound edge is (pre[u], u).
        int u = -1, pre_u;

        // (s, t) is the edge after which the there will be a *undirected* loop (when direction is
        // considered, it's not necessarily a cycle). This can be detected with union-find set.
        int s = 1, t = 1;

        for (int i = 0; i < n; ++i) {
            if (pre[edges[i][1]] == -1) {
                pre[edges[i][1]] = edges[i][0];
            } else {
                u = edges[i][1];
                pre_u = edges[i][0];
            }
            if (!merge(edges[i][0], edges[i][1])) {
                s = edges[i][0];
                t = edges[i][1];
            }
        }

        // Detect the whether there is a directed cycle.
        int lp = s;
        while (pre[lp] != s && pre[lp] != -1) {
            lp = pre[lp];
        }

        if (pre[lp] == -1) { // no cycle
            // When there is no cycle, the second inbound edge to u
            // is an "cross edge", remove it.
            return {pre_u, u};
        } else { // there is cycle
            if (u == -1) {
                // There is no node with indegree as 2, so the last edge added that
                // made it a cycle should be removed.
                return {s, t};
            } else {
                // There is a node with indegree as 2, remove its inbound edge that's
                // on the cycle.
                return {pre[u], u};
            }
        }

        // This will never be reached.
        return {};
    }

private:
    int find(int u) {
        if (parent[u] != u) {
            parent[u] = find(parent[u]);
        }
        return parent[u];
    }

    bool merge(int u, int v) {
        u = find(u);
        v = find(v);
        if (u != v) {
            parent[u] =v;
            return true;
        }
        return false;
    }

    vector<int> parent;
};
```

