class Solution {
    //685. Redundant Connection II
    public int[] findRedundantDirectedConnection(int[][] edges) {
        if(edges==null||edges.length==0) return null;
        //record all edges inside the ring
        Set<Integer> s=new HashSet<>();
        int[] degree=new int[edges.length+1];
        while(true){
            boolean flag=false;
            for (int[] edge : edges) {
                if (!s.contains(edge[0]) && !s.contains(edge[1])) {
                    degree[edge[0]] += 1;
                    degree[edge[1]] += 1;
                }
            }
            for(int i=1;i<=edges.length;++i){
                if(degree[i]==1){
                    s.add(i);
                    flag=true;
                }
                degree[i]=0;//clear
            }
            if(!flag) break;
        }

        int[] inDegree=new int[edges.length+1];
        for (int[] edge : edges) {
            inDegree[edge[1]] += 1;//in-degree
        }
        int[] ans=new int[2];
        for(int i=edges.length-1;i>=0;--i){
            if(!s.contains(edges[i][0])&&!s.contains(edges[i][1])){//a node has multiple parent
                if(inDegree[edges[i][1]]>1){
                    ans[0]=edges[i][0];
                    ans[1]=edges[i][1];
                    return ans;
                }
            }
        }

        //reach here, it shows that no node has multiple parent in the graph. just delete the first matching edge from back to front.
        for(int i=edges.length-1;i>=0;--i){
            if(!s.contains(edges[i][0])&&!s.contains(edges[i][1])){
                ans[0]=edges[i][0];
                ans[1]=edges[i][1];
                return ans;
            }
        }
        return null;
    }
}
