        indegree = defaultdict(list)

        for edge in edges:
            indegree[edge[1]].append(edge[0])

        cand1, cand2 = None, None

        for k, v in indegree.items():

            if len(v) == 2:
                cand1 = [v[0], k]
                cand2 = [v[1], k]
