            parent_x = find_parent(x)
            parent_y = find_parent(y)

            parent[parent_x] = parent_y

        def find_parent(x):

            if parent[x] == -1 : return x
            return find_parent(parent[x])

        parent = [-1]*(len(edges) + 1)

        cycle_edge = None

        for edge in edges:

            if edge == cand2 : continue

            if find_parent(edge[0]) != find_parent(edge[1]) : union(edge[0], edge[1])
            else:
                cycle_edge = edge

        if not cycle_edge : return cand2

        elif not cand1 and not cand2 : return cycle_edge

        elif cycle_edge == cand1 or cycle_edge == cand2 : return cycle_edge

