class Solution:
    def findRedundantDirectedConnection(self, edges: List[List[int]]) -> List[int]:
        # idea: 利用union find判断是否有环，利用parents数组判断某个node是否有多个parents
        uf = UnionFind()
        parents = {}

        first, second, cycle_edge = None, None, None

        for parent, child in edges:
            # in case there is two parents associated with this child
            if child in parents:
                first = [parents[child], child]
                second = [parent, child]
            else:
                parents[child] = parent
                # in case there is a cycle
                if uf.connected(parent, child):
                    cycle_edge = [parent, child]

                uf.union(parent, child)

        if cycle_edge:
            # case 1: there is a cycle and no two parents associated
            # with the same child, return cycle_edge
            # case 2: there is a cycle and are two parents associated
            # with the same child, return first edge that associated with that child
            return first or cycle_edge
        else:
            # case 3: there is no cycle, just two parents associated with the same child
            # then return second
            return second

class UnionFind(object):
    def __init__(self):
        self._ids = {}
        self._ranks = collections.defaultdict(lambda : 1)

    def _find(self, id):
        if id not in self._ids:
            self._ids[id] = id
            return id

        while id != self._ids[id]:
            self._ids[id] = self._ids[self._ids[id]] # path compression
            id = self._ids[id]

        return id

    def connected(self, p, q):
        return self._find(p) == self._find(q)

    def union(self, p, q):
        root_p, root_q = self._find(p), self._find(q)
        if root_p == root_q:
            return

        rank_p, rank_q = self._ranks[root_p], self._ranks[root_q]

        # Union by Rank
        if rank_p <= rank_q:
            self._ids[root_p] = root_q
            self._ranks[root_q] += rank_p
        else:
            self._ids[root_q] = root_p
            self._ranks[root_p] += rank_q
