            id = self._ids[id]

        return id

    def connected(self, p, q):
        return self._find(p) == self._find(q)

    def union(self, p, q):
        root_p, root_q = self._find(p), self._find(q)
        if root_p == root_q:
            return

        rank_p, rank_q = self._ranks[root_p], self._ranks[root_q]

        # Union by Rank
        if rank_p <= rank_q:
            self._ids[root_p] = root_q
            self._ranks[root_q] += rank_p
        else:
            self._ids[root_q] = root_p
            self._ranks[root_p] += rank_q
```

