var findRedundantDirectedConnection = function(edges) {
    //Broken Graphs
    //1. Graph has no loop, a node with 2 parents
    //2. Graph with loop, every node has 1 parent
    //3. Graph with loop, a node with 2 parents

    var nodes = {};
    var parent = {};
    var visited = {}; //key current 'node', node visited 'from'
    let twoParentNode = null;
    //examine every directed edge, try to find a node with 2 parents
    //build tree as we go along
    for(let i=0; i<edges.length;i++){
        let edge = edges[i];
        let fromNode = edge[0];
        let toNode = edge[1];

        if(!nodes[fromNode]){
            nodes[fromNode] = new Node(fromNode);
        }

        if(!nodes[toNode]){
            nodes[toNode] = new Node(toNode);
        }

        if(!visited[toNode]){ //if we are going to a node we have never seen before
            visited[toNode] = fromNode; //mark the node as visited from this parent

            parent[toNode] = [fromNode];
            nodes[fromNode].children.push(toNode);

        }else if(visited[toNode]){ //if we have visited the node we are going to visit before, break
            twoParentNode = [];
            twoParentNode.push([visited[toNode], toNode])
            twoParentNode.push([fromNode, toNode])
            parent[toNode].push(fromNode);
        }
    }

    //find the loop
    let loop = null;
    for(let i=0; i<edges.length;i++){
        visited = {}; //key current 'node', node visited 'from'
        let edge = edges[i];
        let currNode = edge[0];
        let val = null;
        if(parent[currNode]){
            val = dfs(currNode, parent[currNode][0], visited, nodes, parent);

            if(parent[currNode].length == 2 && val == null){
                 val = dfs(currNode, parent[currNode][1], visited, nodes, parent);
            }

            if(val != null){
                loop = val;
            }
        }

    }

    if(loop == null){
        //return the latest second edge
        return twoParentNode.pop();
    }else{
        //there is a loop
        if(twoParentNode){ //a node has two parents
            let p1 = twoParentNode.pop();
            let p2 = twoParentNode.pop();
            for(var el of loop){
                if(el[0] == p1[0] && el[1] == p1[1]){
                    return p1;
                }else if(el[0] == p2[0] && el[1] == p2[1]){
                    return p2;
                }
            }
        }else{
            return loop[0];
        }
    }

    return null;



};

function dfs(curr, from, visited, nodes){
    let loop = [];

    if(visited[curr]){
        loop.push([from,curr])
        return loop;
    }

    let node = nodes[curr];
    visited[curr] = 1;

    let foundLoop = false;

    if(node.children.length != 0){ //has children, could be part of a cycle
        let val = null;
        for(let child of node.children){
            let val = dfs(child, curr, visited, nodes);
            if(val != null){ //found a path
                foundLoop = true;
                loop.push([from, curr])
                loop = loop.concat(val);
                break;
            }
        }
    }

    if(!foundLoop){
        loop = null
    }

    return loop;

}

var Node = function(_val){
    this.val = _val;
    this.children = [];
}
